  render() {
    const {
      currPage
    } = this.state;
    const onRowSelect = ({ id }, isSelected) => {
      if (isSelected && this.state.selected.length !== 2) {
        this.setState({
          selected: [ ...this.state.selected, id ].sort(),
          currPage: this.refs.table.state.currPage
        });
