    PropTypes.number,
    PropTypes.object,
    PropTypes.string
  ]).isRequired,
  valueKey: PropTypes.string.isRequired
}

OptionGroupRenderer.defaultProps = {
  optionDisabledKey: 'isDisabled'
}

export default OptionGroupRenderer

