    super(props);

    this.state = {
      opacity: { x: 0 },
      navBackTextOpacity: { x: 0 },
      navBackTextVisible: false
    };

    this.fadeIn = this.fadeIn.bind(this);
    this.fadeOut = this.fadeOut.bind(this);
    this.tweenUpdate = this.tweenUpdate.bind(this);
  }

  fadeIn() {
    let newOpacity = { x: 1 };
