class XAutosuggest extends React.Component {
	callInputRef = autosuggest => {
		var {inputRef, autosuggestRef} = this.props;
		if (inputRef) {
			inputRef(autosuggest && autosuggest.input);
		}
		if (autosuggestRef) {
			autosuggestRef(autosuggest);
