  losses: number;

  constructor() {
    // Initialize wins and losses to 0
    this.wins = 0;
    this.losses = 0;
  }

  // Increment the win count
  recordWin() {
    this.wins++;
  }

  // Increment the loss count
  recordLoss() {
    this.losses++;
  }

  // Calculate the win percentage
  calculateWinPercentage(): number {
    if (this.wins + this.losses === 0) {
