      {!hasUnkownDevices && !disabled && (<Flexbox>
        <Button bsStyle="primary" onClick={this.clickRefresh}>
          <FAIcon name="refresh" />
        </Button>
      </Flexbox>)}
    </Flexbox>);
  }
