  };

  onInviteUrlByClick = () => {
    InviteUserByLinkActions.show(this.state.group);
    InviteUserActions.hide();
  };

  onKeyDown = (event) => {
    if (event.keyCode === KeyCodes.ESC) {
      event.preventDefault();
      this.onClose();
