        case ActivityTypes.GROUP_PROFILE:
          activityBody = <GroupProfile group={activity.group}/>;
          break;
        default:
      }

      return (
        <section className={activityClassName}>
          {activityBody}
        </section>
      );
    } else {
      return null;
    }
  }

  onChange = () => {
    this.setState(getStateFromStores());
  };
