export default class EventsModal extends AuthComponent {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div>
        <Modal show={this.props.showEvents} onHide={() => this.props.hideCallback()}>
          <Modal.Body>
            <h3>
              <div className="text-center">Events</div>
            </h3>
            <hr/>
