    r ^= modifier;
    g ^= modifier;
    b ^= modifier;
    let hsl = Utilities.RgbToHsl(r, g, b);
    hsl['s'] -= 20; // desaturate a little for that web 3.0 look
    return `hsl(${hsl.h}, ${hsl.s}%, ${hsl.l}%)`;
