    super(props);
    const {
      searchFilter: {
        search_target,
        period,
        sort,
        start_date,
        end_date,
        bookmark_num_min,
        bookmark_num_max,
        bookmarkCountsTag,
      },
    } = props.route.params;
    this.state = {
      target: search_target || 'partial_match_for_tags',
      period: period || SEARCH_PERIOD_TYPES.ALL,
      bookmarkCountsTag: bookmarkCountsTag || '',
      sort: sort || 'date_desc',
      startDate: start_date,
      endDate: end_date,
