      target,
      period,
      sort,
      startDate,
      endDate,
      bookmarkNumMin,
      bookmarkNumMax,
      bookmarkCountsTag,
    });
  };

  render() {
    const { i18n, navigationStateKey, route, theme } = this.props;
    const { word, searchType } = route.params;
    const {
      selectedFilterType,
      selectedPickerValue,
      filterList,
