            <PXListItem
              key={list.key}
              title={this.getSearchTypeName(list.key)}
              description={this.getSelectedFilterName(list.key, list.options)}
              onPress={() => this.handleOnPressFilterOption(list.key)}
            />
          ))}
        </View>
