                </Tab>
              );
            })}
          </TabList>
          {this.state.tabs.map((tab, i) => {
            return <TabPanel key={i}>{tab.content}</TabPanel>;
          })}
        </Tabs>
        <Modal
          isOpen={this.state.isModalOpen}
          onRequestClose={this.closeModal}
          style={{width: 400, height: 350, margin: '0 auto'}}
