    this.setState({
      isModalOpen: false
    });
  },

  addTab() {
    const label = this.refs.label.value;
    const content = this.refs.content.value;

    this.state.tabs.push({
      label: label,
      content: content
    });
    this.setState({
      selectedIndex: this.state.tabs.length - 1
    });
    this.closeModal();
  },

  removeTab(index) {
    this.state.tabs.splice(index, 1);
    this.forceUpdate();
  }
});

