  return {
    onLoadProjects: function (user) {
      dispatch(fetchUserProjects(user));
    },
    fetchProjects: (userId) => {
      dispatch(fetchUserProjects(userId))
    },
    setCurrentProject: (project) => {
      dispatch(setCurrentProject(project));
    },
    logMeOut: function () {
      dispatch(logUserOut());
    },
    nullProjects: () => {
      dispatch(clearProjects());
    },
