    var that = this;
    return (
      <div className="row pie-chart">
        <div className="col-md-3">
          <ul className="pie-legend">
            <li>
              <span></span>
              {this.state.labels[0]}
            </li>
            <li>
              <span></span>
              {this.state.labels[1]}
            </li>
            <li>
              <span></span>
              {this.state.labels[2]}
            </li>
            <li>
              <span></span>
              {this.state.labels[3]}
            </li>
          </ul>
        </div>
        <div className="col-md-9">
          <Pie ref="barChart" data={this.state.chartData} options={this.state.options} />
        </div>
      </div>
    );
  }
