    if (!this.handlers[eventName]) {
      this.handlers[eventName] = [];
    }
    this.handlers[eventName].push(handler);
  }

  emit(eventName, data) {
    if (this.handlers[eventName]) {
      this.handlers[eventName].forEach(handler => handler(data));
