        //   isInverse: false   // 
        // }
      ]
    };
  }

  inverse(index){              // 
    return () => {
      let imgsArrangeArr = this.state.imgsArrangeArr;
      imgsArrangeArr[index].isInverse = !imgsArrangeArr[index].isInverse;

      this.setState({
        imgsArrangeArr: imgsArrangeArr
      });
    }
  }

  // 
  rearrange(centerIndex){
    let arrangeImgArr = this.state.imgsArrangeArr,
        Constant = this.Constant,
        centerPos = Constant.centerPos,
        hLeftSecRangeX = Constant.hsec.hLeftSecX,
        hRightSecRangeX = Constant.hsec.hRightSecX,
        hY = Constant.hsec.hY,
        vLeftX = Constant.vsec.leftX,      // 
        vTopY = Constant.vsec.topY;

        // 
       let centerImgPosArr = arrangeImgArr.splice(centerIndex,1);  // 
        centerImgPosArr[0] = {
          pos : centerPos           // ,
        };

        // 
        let imgTopArr = [],  //
            topNum = Math.floor(Math.random()*2);   // 01

        let topImgIndex = Math.floor(Math.random()*(arrangeImgArr.length - topNum));   // 
            imgTopArr = arrangeImgArr.splice(topImgIndex,topNum);   // 

        imgTopArr && imgTopArr.forEach((value,index)=>{     // 
          imgTopArr[index] = {
            pos:{
              top: getRandomPos(vTopY[0],vTopY[1]),
              left: getRandomPos(vLeftX[0],vLeftX[1])
            },
            rotate: getRandomRotate()
          }
        });

    // 
    for (let i=0,len=arrangeImgArr.length,k=len/2;i<len;i++){
      let secLOR = null;        // x
      if(i<k){   // 
        secLOR = hLeftSecRangeX;
      }else {
        secLOR = hRightSecRangeX;
      }
      arrangeImgArr[i]={
        pos : {
          top: getRandomPos(hY[0],hY[1]),
          left: getRandomPos(secLOR[0],secLOR[1])
        },
        rotate: getRandomRotate()
      }

    }

    //arrangeImgArr
    if(imgTopArr && imgTopArr){        // 
      arrangeImgArr.splice(topImgIndex,0,imgTopArr[0]);
    }
    arrangeImgArr.splice(centerIndex,0,centerImgPosArr[0]);      // 

    this.setState({
      imgsArrangeArr: arrangeImgArr
    });
  }


  componentDidMount(){       // 
    let stageDom = ReactDOM.findDOMNode(this.refs.stage),   // dom
        stageWidth = stageDom.scrollWidth,   // 
        stageHeight = stageDom.scrollHeight,    // 
