  handleLinkClick = (url, text, title) => {
    WBRPCRenderer.wavebox.openExternal(url)
  }

  /* **************************************************************************/
  // Rendering
  /* **************************************************************************/

  shouldComponentUpdate (nextProps, nextState) {
    return shallowCompare(this, nextProps, nextState)
  }

  render () {
    const {
