                    alerts.map((alert, i) => <EmergencyAlert {...alert} theme={theme} key={`alert-nested--${i}`} />)
                  }
                </div>
              </div>
            </Collapse>
            <div className={interfaceClasses}>
              {buttonAlert && <ButtonAlert {...buttonAlert} onClick={handleClick} isOpen={state.open} />}
            </div>
          </>
        )}
      </section>
    </Collapse>
  );
};

