const Broadcast = React.createClass({

  getInitialState() {
    return {
      video: {},
      videoKey: this.props.params.videoKey
    }
  },

  componentDidMount() {
    firebase.database()
      .ref(`/broadcasts/${this.state.videoKey}`)
      .on( 'value', snapshot => {
        const video = snapshot.val();
        this.setState({ video });
        this._setVideoState(video);
