        <label>Realtime</label>
      </div>
    </div>
    <table width="100%" style={{borderSpacing:2}}>
      <tbody>
        <tr>
          <td width="50%" style={{border:'2px solid #999', verticalAlign:'top'}}>
            {component.view(forward(signal, Action.UpdateComponent(component)), model.component)}
